mapper.select:
SELECT ID, DICT_SORT, DICT_LABEL, DICT_VALUE, DICT_TYPE, IS_DEFAULT, STATUS, CREATE_TIME, CREATE_USER, UPDATE_TIME, UPDATE_USER, REMARK
FROM TB_SYS_DICT_DATA


      AND DICT_TYPE = #{dictType}


      AND DICT_LABEL like '%' || #{dictLabel} || '%'


      AND STATUS = #{status}

SELECT ID, DICT_SORT, DICT_LABEL, DICT_VALUE, DICT_TYPE, IS_DEFAULT, STATUS, CREATE_TIME, CREATE_USER, UPDATE_TIME, UPDATE_USER, REMARK
FROM TB_SYS_DICT_DATA
where ID = #{id}

mapper.update:
update TB_SYS_DICT_DATA
dict_sort = #{dictSort}, dict_label = #{dictLabel}, dict_value = #{dictValue}, dict_type = #{dictType}, is_default = #{isDefault}, status = #{status}, remark = #{remark}, update_user = #{createUser}, update_time = sysdate
where ID = #{id}
