mapper.select:
SELECT ID, TITLE, BUSINESS_TYPE, METHOD, OPERATOR_TYPE, OPER_NAME, OPER_URL, OPER_IP, OPER_LOCATION, OPER_PARAM, STATUS, ERROR_MSG, OPER_TIME FROM TB_SYS_OPER_LOG
AND TITLE LIKE CONCAT('%', #{title}, '%')
AND BUSINESS_TYPE = #{businessType}
AND STATUS = #{status}
AND OPER_NAME like concat('%', #{operName}, '%')
